// App.jsx
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Home from './pages/Home';
import JobList from './components/JobList';
import PostJob from './pages/JobPost';
import EditJob from './pages/EditJob';
import ViewJob from './pages/ViewJob';

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('dark', darkMode);
  }, [darkMode]);

  return (
    <Router>
      <div className={`min-h-screen font-sans ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
        <header className={`shadow-md px-6 py-4 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="max-w-7xl mx-auto flex justify-between items-center">
            <h1 className="text-3xl font-extrabold text-blue-600">JobPortal</h1>
            <div className="flex gap-6 items-center">
              <nav className="space-x-6 text-lg">
                <Link to="/" className="hover:text-blue-500 transition">Home</Link>
                <Link to="/jobs" className="hover:text-blue-500 transition">Jobs</Link>
                <Link to="/post" className="hover:text-blue-500 transition">Post a Job</Link>
              </nav>
              <button
                className="px-3 py-1 rounded-md text-sm bg-blue-500 text-white hover:bg-blue-600 transition"
                onClick={() => setDarkMode(!darkMode)}
              >
                {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
              </button>
            </div>
          </div>
        </header>

        <main className="max-w-6xl mx-auto py-10 px-4">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/jobs" element={<JobList />} />
            <Route path="/post" element={<PostJob />} />
            <Route path="/edit/:id" element={<EditJob />} />
            <Route path="/view/:id" element={<ViewJob />} />
          </Routes>
        </main>


      </div>
    </Router>
  );
};

export default App;
