// App.jsx
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Home from './pages/Home';
import JobList from './components/JobList';
import PostJob from './pages/JobPost';
import EditJob from './pages/EditJob';
import ViewJob from './pages/ViewJob';

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('dark', darkMode);
  }, [darkMode]);

  const navLinkClass = `px-4 py-2 rounded-md transition font-medium border border-gray-300 dark:border-gray-600 shadow-sm hover:bg-blue-100 dark:hover:bg-gray-700 hover:text-blue-600 dark:hover:text-white`;

  return (
    <Router>
      <div className={`min-h-screen font-sans ${darkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
        <header className={`shadow-md px-8 py-6 ${darkMode ? 'bg-gray-800' : 'bg-white'}`}>
          <div className="max-w-7xl mx-auto flex justify-between items-center">
            <h1 className="text-4xl font-black text-blue-600 tracking-tight">JobPortal</h1>
            <div className="flex gap-4 items-center">
              <nav className="flex gap-4">
                <Link to="/" className={navLinkClass}>Home</Link>
                <Link to="/jobs" className={navLinkClass}>Browse Jobs</Link>
                <Link to="/post" className={navLinkClass}>Post a Job</Link>
                <Link to="/edit/1" className={navLinkClass}>Edit Job</Link>
              </nav>
              <button
                className="px-4 py-2 rounded-md text-sm font-semibold bg-blue-500 text-white hover:bg-blue-600 transition shadow-md"
                onClick={() => setDarkMode(!darkMode)}
              >
                {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
              </button>
            </div>
          </div>
        </header>

        <main className="max-w-6xl mx-auto py-12 px-6">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/jobs" element={<JobList />} />
            <Route path="/post" element={<PostJob />} />
            <Route path="/edit/:id" element={<EditJob />} />
            <Route path="/view/:id" element={<ViewJob />} />
          </Routes>
        </main>


      </div>
    </Router>
  );
};

export default App;
