// App.jsx
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

import Home from './pages/Home';
import JobList from './components/JobList';       // ‚úÖ Fixed path
import PostJob from './pages/JobPost';            // ‚úÖ Fixed path
import EditJob from './pages/EditJob';
import ViewJob from './pages/ViewJob';

const App = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('dark', darkMode);
  }, [darkMode]);

  return (
    <Router>
      <div className="min-h-screen">
        <header className="shadow px-6 py-4 bg-white dark:bg-gray-800">
          <div className="container flex justify-between items-center">
            <h1 className="text-2xl font-bold text-primary">JobPortal</h1>
            <div className="flex gap-4 items-center">
              <nav className="space-x-4">
                <Link to="/">Home</Link>
                <Link to="/jobs">Jobs</Link>
                <Link to="/post">Post a Job</Link>
              </nav>
              <button className="toggle-btn" onClick={() => setDarkMode(!darkMode)}>
                {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
              </button>
            </div>
          </div>
        </header>

        <main className="py-8 px-4">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/jobs" element={<JobList />} />
            <Route path="/post" element={<PostJob />} />
            <Route path="/edit/:id" element={<EditJob />} />
            <Route path="/view/:id" element={<ViewJob />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
};

export default App;
